mapper.select:
select * from st_settle_record


    settle_account_id = #{record.settleAccountId,jdbcType=CHAR}


    and stock_id = #{record.stockId,jdbcType=CHAR}


    and deal_type = #{record.dealType,jdbcType=SMALLINT}


    and date_time >= #{startTime,jdbcType=BIGINT}


    and date_time <= #{endTime,jdbcType=BIGINT}


order by date_time desc
limit #{offset, jdbcType=INTEGER},#{limit, jdbcType=INTEGER}

update.foreach:
update st_settle_record
settle_account_id = #{item.settleAccountId,jdbcType=CHAR}, stock_id = #{item.stockId,jdbcType=CHAR}, quote_price = #{item.quotePrice,jdbcType=DECIMAL}, amount = #{item.amount,jdbcType=BIGINT}, deal_fee = #{item.dealFee,jdbcType=DECIMAL}, deal_type = #{item.dealType,jdbcType=SMALLINT}, date_time = #{item.dateTime,jdbcType=BIGINT}
where settle_record_id = #{item.settleRecordId,jdbcType=CHAR}
