mapper.select:
select * from st_trade_record


    seller_account_id = #{record.sellerAccountId,jdbcType=CHAR}


    or buyer_account_id = #{record.buyerAccountId,jdbcType=CHAR}


    and stock_id = #{record.stockId,jdbcType=CHAR}


    and date_time >= #{startTime,jdbcType=BIGINT}


    and date_time <= #{endTime,jdbcType=BIGINT}


order by date_time desc
limit #{offset, jdbcType=INTEGER},#{limit, jdbcType=INTEGER}

update.foreach:
update st_trade_record
seller_account_id = #{item.sellerAccountId,jdbcType=CHAR}, buyer_account_id = #{item.buyerAccountId,jdbcType=CHAR}, stock_id = #{item.stockId,jdbcType=CHAR}, quote_price = #{item.quotePrice,jdbcType=DECIMAL}, amount = #{item.amount,jdbcType=BIGINT}, date_time = #{item.dateTime,jdbcType=BIGINT}, buy_fee = #{item.buyFee,jdbcType=DECIMAL}, sell_fee = #{item.sellFee,jdbcType=DECIMAL}, deal_tax = #{item.dealTax,jdbcType=DECIMAL}
where record_id = #{item.recordId,jdbcType=CHAR}
